CREATE TABLE aznews.category_news
(
    id varchar(50) PRIMARY KEY NOT NULL,
    version bigint(20) NOT NULL,
    is_deleted bit(1),
    last_modified_user varchar(255),
    last_modified_time datetime,
    category_id varchar(100),
    news_id varchar(100)
);
CREATE INDEX category_news_category_id ON aznews.category_news (category_id);
CREATE INDEX category_news_news_id ON aznews.category_news (news_id);
